mapper.select:
select

  distinct


from select_config




  order by ${orderByClause}



    limit ${limitStart} , ${limitEnd}


    limit ${limitEnd}



  limit ${limit.start} , ${limit.size}

mapper.insert:
insert into select_config (id, tenant_id, object_field_id, 
  value, label, sequence, 
  is_system, create_user_id, create_date, 
  update_user_id, update_date, is_delete
  )
values (#{id,jdbcType=BIGINT}, #{tenantId,jdbcType=BIGINT}, #{objectFieldId,jdbcType=BIGINT}, 
  #{value,jdbcType=INTEGER}, #{label,jdbcType=VARCHAR}, #{sequence,jdbcType=INTEGER}, 
  #{isSystem,jdbcType=TINYINT}, #{createUserId,jdbcType=BIGINT}, #{createDate,jdbcType=TIMESTAMP}, 
  #{updateUserId,jdbcType=BIGINT}, #{updateDate,jdbcType=TIMESTAMP}, #{isDelete,jdbcType=TINYINT}
  )
