mapper.select:
select

  distinct


from product_nav




  order by ${orderByClause}



    limit ${limitStart} , ${limitEnd}


    limit ${limitEnd}



  limit ${limit.start} , ${limit.size}

mapper.insert:
insert into product_nav (id, tenant_id, product_id, 
  unit_nav, total_nav, up_down_rate, 
  nav_date, create_user_id, create_date, 
  update_user_id, update_date, is_delete
  )
values (#{id,jdbcType=BIGINT}, #{tenantId,jdbcType=BIGINT}, #{productId,jdbcType=BIGINT}, 
  #{unitNav,jdbcType=DOUBLE}, #{totalNav,jdbcType=DOUBLE}, #{upDownRate,jdbcType=DOUBLE}, 
  #{navDate,jdbcType=DATE}, #{createUserId,jdbcType=BIGINT}, #{createDate,jdbcType=TIMESTAMP}, 
  #{updateUserId,jdbcType=BIGINT}, #{updateDate,jdbcType=TIMESTAMP}, #{isDelete,jdbcType=TINYINT}
  )
